mapper.select:
SELECT p.id, p.title, p.createdAt, u.id AS userId, u.username, c.id AS categoryId, c.cateName FROM Post p LEFT JOIN User u ON p.userId = u.id LEFT JOIN Category c ON p.categoryId = c.id
cateName = #{category}
GROUP BY p.id, p.title, p.createdAt, u.id, u.username, c.id, c.cateName ORDER BY p.createdAt LIMIT 10 OFFSET #{offset};
